mapper.sql:
FROM 
	mini.incodegroup a
WHERE 1=1
   AND incgDelNy = #{shDelNy}
   AND incgUseNy = #{shUseNy}

	    AND incgSeq=#{shValue}
	    AND incgName=#{shValue}
	    AND incgOrder=#{shValue}



		AND a.incgRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
		AND a.incgEdiDate BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.update:
    UPDATE incodegroup
    SET
  	    incgName = #{incgName}
		,incgUseNy = #{incgUseNy}
		,incgOrder = #{incgOrder}
		,incgDesc = #{incgDesc}
		,incgDelNy = #{incgDelNy}
	WHERE 1=1
		AND	incgSeq = #{incgSeq}

mapper.delete:
DELETE FROM incodegroup
WHERE 1=1
	AND incgSeq = #{incgSeq}
