mapper.sql:
	FROM
	   mini.incode AS b
	   JOIN incodegroup AS a
	     on a.incgSeq = b.inCodegroup_incgSeq
    WHERE 1=1
	   AND incdDelNy = #{shDelNy}
	   AND incdUseNy = #{shUseNy}

		    AND incdSeq=#{shValue}
		    AND incdName=#{shValue}
		    AND incdOrder=#{shValue}



			AND a.incdRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
			AND a.incdEdiDate BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.update:
    UPDATE incode
    SET
  	    incdName = #{incdName}
		,incdUseNy = #{incdUseNy}
		,incdOrder = #{incdOrder}
		,incdDesc = #{incdDesc}
		,incdDelNy = #{incdDelNy}
		,inCodegroup_incgSeq = #{inCodegroup_incgSeq}
	WHERE 1=1
		AND	incdSeq = #{incdSeq}

mapper.delete:
DELETE FROM incode
WHERE 1=1
	AND incdSeq = #{incdSeq}
