mapper.select:
SELECT g.inconSeq, g.inconCondition, g.inconSettleDate, g.inconRefund, g.inconRegDate, g.inconEdiDate, g.inconDelNy, g.inApplication_inappSeq, g.inMember_inmeSeq, g.inStaff_instSeq, g.inProduct_inproSeq, e.inappRequType, c.inmeName, c.inmeIdNumberFr, c.inmeIdNumberBa, c.inmeJobNy, d.instId, e.inappRecBankType, e.inappRecAccNo, f.inproName, f.inproGrade, f.inproContent, f.inproStartDate, f.inproEndDate FROM inContract AS g LEFT JOIN inApplication AS e ON g.inApplication_inappSeq = e.inappSeq LEFT JOIN inMember AS c ON e.inMember_inmeSeq = c.inmeSeq LEFT JOIN inStaff AS d ON g.inStaff_instSeq = d.instSeq LEFT JOIN inProduct AS f ON g.inProduct_inproSeq = f.inproSeq WHERE 1=1 AND inconCondition = #{shconCondition}
AND g.inconRegDate BETWEEN #{shDateStart} AND #{shDateEnd} AND g.inconEdiDate BETWEEN #{shDateStart} AND #{shDateEnd}
AND inconSeq = #{shValue} AND inmeName LIKE CONCAT('%',#{shValue},'%') AND inmeTel LIKE CONCAT('%',#{shValue},'%') AND inmeIdNumberFr LIKE CONCAT('%',#{shValue},'%') AND inmeIdNumberBa LIKE CONCAT('%',#{shValue},'%') AND instId LIKE CONCAT('%',#{shValue},'%')
AND inconDelNy = #{shconDelNy} ORDER BY g.inconSeq desc
