mapper.sql:
FROM 
	inContract AS g
	LEFT JOIN inApplication AS e
		ON g.inApplication_inappSeq = e.inappSeq
	LEFT JOIN inMember AS c
		ON e.inMember_inmeSeq = c.inmeSeq
	LEFT JOIN inStaff AS d
		ON g.inStaff_instSeq = d.instSeq
	LEFT JOIN inProduct AS f
		ON g.inProduct_inproSeq = f.inproSeq
WHERE 1=1
	AND inconDelNy = #{shconDelNy}
	AND inconCondition = #{shconCondition}
	AND inproGrade = #{shproGrade}

		AND g.inconSettleDate BETWEEN #{shDateStart} AND #{shDateEnd}
		AND f.inproStartDate BETWEEN #{shDateStart} AND #{shDateEnd}
		AND f.inproEndDate BETWEEN #{shDateStart} AND #{shDateEnd}
		AND g.inconRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
		AND g.inconEdiDate BETWEEN #{shDateStart} AND #{shDateEnd}


		AND inconSeq = #{shValue}
		AND inproName LIKE CONCAT('%',#{shValue},'%')
		AND inmeName LIKE CONCAT('%',#{shValue},'%')
		AND inmeIdNumberFr LIKE CONCAT('%',#{shValue},'%')
		AND inmeIdNumberBa LIKE CONCAT('%',#{shValue},'%')
		AND instId LIKE CONCAT('%',#{shValue},'%')
