mapper.sql:
FROM 
	mini.inqnaboard l
WHERE 1=1
   AND inqbDelNy = #{shDelNy}
   AND inqbCompleteNy = #{shCompleteNy}

	    AND inqbSeq=#{shValue}
	    AND inqbTitle=#{shValue}
	    AND inqbContent=#{shValue}



		AND a.inqbRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
		AND a.inqbEdiDate BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.update:
    UPDATE inqnaboard
    SET
  	    inqbTitle = #{inqbTitle}
		,inqbContent = #{inqbContent}
		,inqbCompleteNy = #{inqbCompleteNy}
		,inqbEdiDate = sysdate()
	WHERE 1=1
		AND	inqbSeq = #{inqbSeq}

mapper.delete:
DELETE FROM inqnaboard
WHERE 1=1
	AND inqbSeq = #{inqbSeq}
