mapper.sql:
FROM 
	mini.inqnaboard AS l
	LEFT JOIN inmember AS c
     on c.inmeSeq = l.inMember_inmeSeq
   LEFT JOIN inStaff AS d
     on d.instSeq = l.inStaff_instSeq
WHERE 1=1
   AND inqbDelNy = #{shDelNy}
   AND inqbCompleteNy = #{shCompleteNy}

	    AND inqbSeq=#{shValue}
	    AND inqbTitle LIKE CONCAT('%',#{shValue},'%')
	    AND inqbType LIKE CONCAT('%',#{shValue},'%')
	    AND inmeId LIKE CONCAT('%',#{shValue},'%')
	    AND instId LIKE CONCAT('%',#{shValue},'%')



		AND l.inqbRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
		AND l.inqbEdiDate BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.update:
    UPDATE inqnaboard
    SET
  	    inqbTitle = #{inqbTitle}
		,inqbContent = #{inqbContent}
		,inqbReContent = #{inqbReContent}
		,inqbCompleteNy = #{inqbCompleteNy}
		,inqbEdiDate = sysdate()
	WHERE 1=1
		AND	inqbSeq = #{inqbSeq}

mapper.delete:
DELETE FROM inqnaboard
WHERE 1=1
	AND inqbSeq = #{inqbSeq}
